import React, { Component } from 'react';
// import { DropdownButton, MenuItem } from 'react-bootstrap';
import List from './List';

class FilteredList extends Component {
  constructor(props) {
    super(props);

    //The state is just a list of key/value pairs (like a hashmap)
    //TODO (FilteredList): Add an additional state variable within this.state called "type" and set it to a default value
    this.state = {
      search: "",
      type: "all"
    };
  }

  //Sets the state whenever the user types on the search bar
  onSearch = (event) => {
    this.setState({search: event.target.value.trim().toLowerCase()});
  }

  //TODO (FilteredList): Set the state of the "type" state variable depending on what is passed in
  onFilter = (event) => {
    this.setState({type});
  }

  //TODO (FilteredList): Change filterItem to take into account the "type" state variable when filtering
  filterItem = (item) => {
      if (this.state.type !== "all" && item.type.toLowerCase() !== this.state.type) {
        return false;
      } 
      return item.name.toLowerCase().search(this.state.search) !== -1;
  }

  render(){
    return (
        <div className = "filter-list">

          <DropdownButton id="typeDropdown" title={"Type"}>
            <MenuItem eventKey="all" onSelect={this.onFilter}>All</MenuItem>
            <MenuItem eventKey="fruit" onSelect={this.onFilter}>Fruit</MenuItem>
            <MenuItem eventKey="vegetable" onSelect={this.onFilter}>Vegetables</MenuItem>
          </DropdownButton>

          <input type = "text" placeholder = "Search" onChange = {this.onSearch} />
          <List items = {this.props.items.filter(this.filterItem)} />
        </div>
    );
  }
}

export default FilteredList;
